const express = require('express');
const app = express();
const PORT = process.env.PORT || 3000;

// Middlewares
app.use(express.json()); // Para aceptar JSON en las peticiones

// Datos simulados (en memoria, luego se usará base de datos)
let productos = [
  { id: 1, nombre: 'Pan francés', precio: 1.5, cantidad: 100, descripcion: 'Pan clásico y crujiente.' },
  { id: 2, nombre: 'Croissant', precio: 2.0, cantidad: 50, descripcion: 'Delicioso croissant de mantequilla.' }
];

// Controladores (Single Responsibility Principle)
function listarProductos(req, res) {
  res.json(productos);
}

function crearProducto(req, res) {
  const { nombre, precio, cantidad, descripcion } = req.body;
  if (!nombre || precio == null || cantidad == null || !descripcion) {
    return res.status(400).json({ error: 'Todos los campos son obligatorios.' });
  }
  const nuevoProducto = {
    id: productos.length ? productos[productos.length - 1].id + 1 : 1,
    nombre,
    precio,
    cantidad,
    descripcion
  };
  productos.push(nuevoProducto);
  res.status(201).json(nuevoProducto);
}

function actualizarProducto(req, res) {
  const { id } = req.params;
  const { nombre, precio, cantidad, descripcion } = req.body;
  const producto = productos.find(p => p.id === parseInt(id));
  if (!producto) {
    return res.status(404).json({ error: 'Producto no encontrado.' });
  }
  if (!nombre || precio == null || cantidad == null || !descripcion) {
    return res.status(400).json({ error: 'Todos los campos son obligatorios.' });
  }
  producto.nombre = nombre;
  producto.precio = precio;
  producto.cantidad = cantidad;
  producto.descripcion = descripcion;
  res.json(producto);
}

function eliminarProducto(req, res) {
  const { id } = req.params;
  const index = productos.findIndex(p => p.id === parseInt(id));
  if (index === -1) {
    return res.status(404).json({ error: 'Producto no encontrado.' });
  }
  const eliminado = productos.splice(index, 1);
  res.json(eliminado[0]);
}

// Rutas
app.get('/', (req, res) => {
  res.send('¡Hola mundo desde el backend de la panadería!');
});
app.get('/api/productos', listarProductos);
app.post('/api/productos', crearProducto);
app.put('/api/productos/:id', actualizarProducto);
app.delete('/api/productos/:id', eliminarProducto);

// Aquí se agregarán las demás rutas CRUD siguiendo buenas prácticas

app.listen(PORT, () => {
  console.log(`Servidor escuchando en http://localhost:${PORT}`);
});

