import React, { useEffect, useState } from 'react';
import { API_URL } from './api';

function ListaProductos() {
  const [productos, setProductos] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState(null);
  const [editandoId, setEditandoId] = useState(null);
  const [formEdit, setFormEdit] = useState({ nombre: '', precio: '', cantidad: '', descripcion: '' });

  const fetchProductos = () => {
    setCargando(true);
    fetch(API_URL)
      .then(res => {
        if (!res.ok) throw new Error('Error al obtener productos');
        return res.json();
      })
      .then(data => {
        setProductos(data);
        setCargando(false);
      })
      .catch(err => {
        setError(err.message);
        setCargando(false);
      });
  };

  useEffect(() => {
    fetchProductos();
  }, []);

  const handleEliminar = async (id) => {
    if (!window.confirm('¿Seguro que deseas eliminar este producto?')) return;
    setError(null);
    try {
      const res = await fetch(`${API_URL}/${id}`, { method: 'DELETE' });
      if (!res.ok) throw new Error('Error al eliminar producto');
      fetchProductos();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleEditar = (prod) => {
    setEditandoId(prod.id);
    setFormEdit({
      nombre: prod.nombre,
      precio: prod.precio,
      cantidad: prod.cantidad,
      descripcion: prod.descripcion
    });
  };

  const handleEditChange = e => {
    setFormEdit({ ...formEdit, [e.target.name]: e.target.value });
  };

  const handleEditSubmit = async e => {
    e.preventDefault();
    setError(null);
    // Validación básica
    if (!formEdit.nombre.trim() || !formEdit.precio || !formEdit.cantidad || !formEdit.descripcion.trim()) {
      setError('Todos los campos de edición son obligatorios.');
      return;
    }
    if (isNaN(formEdit.precio) || parseFloat(formEdit.precio) <= 0) {
      setError('El precio debe ser un número mayor a 0.');
      return;
    }
    if (isNaN(formEdit.cantidad) || parseInt(formEdit.cantidad) < 0) {
      setError('La cantidad debe ser un número igual o mayor a 0.');
      return;
    }
    try {
      const res = await fetch(`${API_URL}/${editandoId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          nombre: formEdit.nombre.trim(),
          precio: parseFloat(formEdit.precio),
          cantidad: parseInt(formEdit.cantidad),
          descripcion: formEdit.descripcion.trim()
        })
      });
      if (!res.ok) throw new Error('Error al editar producto');
      setEditandoId(null);
      fetchProductos();
    } catch (err) {
      setError(err.message);
    }
  };


  if (cargando) return <div className="spinner"></div>;
  return (
    <div>
      <h2>Lista de Productos</h2>
      {error && <div className="error">Error: {error}</div>}
      {productos.length === 0 && !cargando && <p>No hay productos registrados.</p>}
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Precio</th>
            <th>Cantidad</th>
            <th>Descripción</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {productos.map(prod => (
            editandoId === prod.id ? (
              <tr key={prod.id}>
                <td>{prod.id}</td>
                <td><input name="nombre" value={formEdit.nombre} onChange={handleEditChange} /></td>
                <td><input name="precio" type="number" step="0.01" value={formEdit.precio} onChange={handleEditChange} /></td>
                <td><input name="cantidad" type="number" value={formEdit.cantidad} onChange={handleEditChange} /></td>
                <td><input name="descripcion" value={formEdit.descripcion} onChange={handleEditChange} /></td>
                <td>
                  <button onClick={handleEditSubmit}>Guardar</button>
                  <button onClick={() => setEditandoId(null)}>Cancelar</button>
                </td>
              </tr>
            ) : (
              <tr key={prod.id}>
                <td>{prod.id}</td>
                <td>{prod.nombre}</td>
                <td>{prod.precio}</td>
                <td>{prod.cantidad}</td>
                <td>{prod.descripcion}</td>
                <td>
                  <button onClick={() => handleEditar(prod)}>Editar</button>
                  <button onClick={() => handleEliminar(prod.id)}>Eliminar</button>
                </td>
              </tr>
            )
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div>
      <h2>Lista de Productos</h2>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Precio</th>
            <th>Cantidad</th>
            <th>Descripción</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {productos.map(prod => (
            editandoId === prod.id ? (
              <tr key={prod.id}>
                <td>{prod.id}</td>
                <td><input name="nombre" value={formEdit.nombre} onChange={handleEditChange} /></td>
                <td><input name="precio" type="number" step="0.01" value={formEdit.precio} onChange={handleEditChange} /></td>
                <td><input name="cantidad" type="number" value={formEdit.cantidad} onChange={handleEditChange} /></td>
                <td><input name="descripcion" value={formEdit.descripcion} onChange={handleEditChange} /></td>
                <td>
                  <button onClick={handleEditSubmit}>Guardar</button>
                  <button onClick={() => setEditandoId(null)}>Cancelar</button>
                </td>
              </tr>
            ) : (
              <tr key={prod.id}>
                <td>{prod.id}</td>
                <td>{prod.nombre}</td>
                <td>{prod.precio}</td>
                <td>{prod.cantidad}</td>
                <td>{prod.descripcion}</td>
                <td>
                  <button onClick={() => handleEditar(prod)}>Editar</button>
                  <button onClick={() => handleEliminar(prod.id)}>Eliminar</button>
                </td>
              </tr>
            )
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ListaProductos;

import React, { useState } from 'react';
import { API_URL } from './api';

function FormularioProducto({ onProductoCreado }) {
  const [form, setForm] = useState({
    nombre: '',
    precio: '',
    cantidad: '',
    descripcion: ''
  });
  const [error, setError] = useState(null);
  const [exito, setExito] = useState(null);
  const [cargando, setCargando] = useState(false);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError(null);
    setExito(null);
    // Validación de campos
    if (!form.nombre.trim() || !form.precio || !form.cantidad || !form.descripcion.trim()) {
      setError('Todos los campos son obligatorios.');
      return;
    }
    if (isNaN(form.precio) || parseFloat(form.precio) <= 0) {
      setError('El precio debe ser un número mayor a 0.');
      return;
    }
    if (isNaN(form.cantidad) || parseInt(form.cantidad) < 0) {
      setError('La cantidad debe ser un número igual o mayor a 0.');
      return;
    }
    setCargando(true);
    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          nombre: form.nombre.trim(),
          precio: parseFloat(form.precio),
          cantidad: parseInt(form.cantidad),
          descripcion: form.descripcion.trim()
        })
      });
      if (!res.ok) throw new Error('Error al crear producto');
      setExito('Producto creado correctamente');
      setForm({ nombre: '', precio: '', cantidad: '', descripcion: '' });
      if (onProductoCreado) onProductoCreado();
    } catch (err) {
      setError(err.message);
    } finally {
      setCargando(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 24 }}>
      <h2>Agregar Producto</h2>
      <div>
        <label>Nombre: <input name="nombre" value={form.nombre} onChange={handleChange} required /></label>
      </div>
      <div>
        <label>Precio: <input name="precio" type="number" step="0.01" value={form.precio} onChange={handleChange} required /></label>
      </div>
      <div>
        <label>Cantidad: <input name="cantidad" type="number" value={form.cantidad} onChange={handleChange} required /></label>
      </div>
      <div>
        <label>Descripción: <input name="descripcion" value={form.descripcion} onChange={handleChange} required /></label>
      </div>
      <button type="submit" disabled={cargando}>Agregar</button>
      {cargando && <div className="spinner"></div>}
      {error && <div className="error">{error}</div>}
      {exito && <div className="success">{exito}</div>}
    </form>
  );
}

export default FormularioProducto;

